public class NestedLoopDemo1 {

	public static void main(String[] args) {
		int i,j;
		for (i=1;i<=5;i++) {
			for(j=1;j<=i;j++) {
				System.out.print("* \t");
			}
			System.out.println();
		}
		// TODO Auto-generated method stub

	}

}
